mapper.update:
UPDATE MEMBER SET
M_STATUS = 'N'
WHERE M_NO = #{m_no}

UPDATE MEMBER SET
M_PW = #{password}
WHERE M_NO = #{m_no}

UPDATE PAY SET
PAY_STATUS = '환불대기중'
WHERE PAY_NO = #{payNo}

UPDATE PAY SET
PAY_STATUS = '배송준비중'
WHERE PAY_NO = #{payNo}

mapper.delete:
DELETE FROM LIKES
WHERE M_NO = #{m_no}
AND C_NO IN

		#{c_no}
